The following example shows an implementation of the Account abstract class.

public class ExampleAccount : Account
{
    public override string Id
    {
        get;
        set;
    }

    public override string Username
    {
        get;
        set;
    }

    public override string UserId
    {
        get;
        set;
    }

    // Gets or sets the token value upon successful OAuth authentication.
    public string Token
    {
        get;
        set;
    }

    // Gets or sets the token secret value upon successful OAuth authentication.
    public string TokenSecret
    {
        get;
        set;
    }

    public override Guid AccountProviderId
    {
        get { return ExampleAccountProvider.ExampleAccountProviderId; }
    }

    public override void UpdateAccount(Account sourceAccount)
    {
        var exampleAccount = (ExampleAccount)sourceAccount;

        this.Id = exampleAccount.Id;
        this.Username = exampleAccount.Username;
        this.UserId = exampleAccount.UserId;

        //Update the rest of the custom properties of ExampleAccount class.
    }
}

