public class ExampleAccountProvider : AccountProvider
{
    public static readonly Guid ExampleAccountProviderId = new Guid("<Enter your guid here>");

    public override Guid Id
    {
        get { return ExampleAccountProviderId; }
    }

    public override DataTemplate NewAccountTemplate
    {
        get 
        {
            //Normally, you would return a resource which identifies the new account data template for all accounts of type ExampleAccount.
            throw new NotImplementedException(); 
        }
    }

    public override DataTemplate EditAccountTemplate
    {
        get
        {
            //Normally, you would return a resource which identifies the edit account data template for all accounts of type ExampleAccount.
            throw new NotImplementedException();
        }
    }

    public override DataTemplate AccountIcon
    {
        get
        {
            //Normally, you would return a resource which identifies the account icon data template for all accounts of type ExampleAccount.
            throw new NotImplementedException();
        }
    }

    public override string AccountTypeName
    {
        get { return "Example Account"; }
    }

    public override string AccountDomain
    {
        get { return "www.example.com"; }
    }

    public override DataTemplate MigrateAccountTemplate
    {
        get
        {
            //Normally, you would return a resource which identifies the migrate account data template for all accounts of type ExampleAccount.
            throw new NotImplementedException();
        }
    }

    public override Account CreateAccount()
    {
        return new ExampleAccount();
    }

    public override void AuthenticateAsync(Account account, object userState, AsyncCompletedCallback callback)
    {
        //Logic for authenticating the account against a service.
    }

    public override string SerializeAccount(Account account)
    {
        //Default xml serialization
        var serializer = new XmlSerializer(typeof(ExampleAccount));

        var stringBuilder = new StringBuilder();
        var xmlWriter = XmlWriter.Create(stringBuilder);

        ExampleAccount exampleAccount = (ExampleAccount)account;
        serializer.Serialize(xmlWriter, exampleAccount);

        return stringBuilder.ToString();
    }

    public override Account DeserializeAccount(string data)
    {
        //Default xml deserialization
        var serializer = new XmlSerializer(typeof(ExampleAccount));

        var account = (ExampleAccount)serializer.Deserialize(new StringReader(data));

        return account;
    }

    public override ISession GetSession(Account account)
    {
        var exampleAccount = (ExampleAccount)account;
        return new ExampleSession(exampleAccount);
    }

    public override ServerAccount GetServerAccount(Account account)
    {
        var exampleAccount = (ExampleAccount)account;

        return new ServerAccount
        {
            Domain = AccountDomain,
            UserId = exampleAccount.UserId.ToString(),
            Username = exampleAccount.Username,
            Token = exampleAccount.Token,
            TokenSecret = exampleAccount.TokenSecret
        };
    }

    public override Account GetAccount(ServerAccount serverAccount)
    {
        return new ExampleAccount()
        {
            UserId = serverAccount.UserId,
            Username = serverAccount.Username,
            Token = serverAccount.Token,
            TokenSecret = serverAccount.TokenSecret,
            ServerAccountId = serverAccount.Id,
            IsAuthenticated = true
        };
    }

    public override void SynchronizeAccounts(Account localAccount, ServerAccount serverAccount)
    {
        var exampleAccount = (ExampleAccount)localAccount;

        exampleAccount.Token = serverAccount.Token;
        exampleAccount.TokenSecret = serverAccount.TokenSecret;
    }

    public override bool AccountNeedsMigration(Account account)
    {
        return false;
    }
}

The following example shows an implementation of the IAutoCompleteProvider interface, called ExampleAutoCompleteProvider.

public class ExampleAutoCompleteProvider : IAutoCompleteProvider
{
    //Regular expresion for Twitter like usernames (@username).
    public static readonly Regex UsernameRegex = new Regex(@"(\B|\A)@([A-Za-z0-9_]+)");

    public Guid Id
    {
        get { return new Guid("<Enter your guid here>"); }
    }

    public static List<string> Words
    {
        get;
        private set;
    }

    public Regex[] RegexTriggers
    {
        get { return new[] { UsernameRegex }; }
    }

    public void FillAutoCompleteContext(AutoCompleteContext context)
    {
        Words.ForEach(w => context.AddKeyword(w));
    }

    public static void AddWords(IEnumerable<string> words)
    {
        Words.AddRange(words);
    }
}

The following example shows how to implement an AutoCompletePostingProcessor by implementing the PostingProcessor abstract class.

public class AutoCompletePostingProcessor : PostingProcessor
{
    public override void PreProcessPost(PostContainer postContainer)
    {

    }

    public override void PostProcessPost(PostContainer postContainer, PostingProvider[] postingProviders)
    {
        var text = postContainer.Text;

        if (string.IsNullOrWhiteSpace(text) == false)
        {
            var keywords = ExampleAutoCompleteProvider.UsernameRegex.Matches(text).OfType<Match>().Select(m => m.Value).Distinct().ToList();
            ExampleAutoCompleteProvider.AddWords(keywords);
        }
    }
}

