    }
}

The following example shows an implementation of the ISession interface, called ExampleSession. Check the implementation of the ExampleSidebarAction class in the  code example.

public class ExampleSession : ISession
{
    private ExampleAccount _Account;

    private ISidebarAction _SidebarAction;

    private Timeline _MainTimeline;

    IAccount ISession.Account
    {
        get { return null; }
    }

    public ExampleAccount Account
    {
        get { return _Account; }
    }

    public ISidebarAction SidebarAction
    {
        get { return _SidebarAction; }
    }

    public void Start()
    {
        //Do work on session start.
    }

    public void Stop()
    {
        //Do work on session end
    }

    public event EventHandler<TimelineItemEventArgs> ItemAdded;

    public event EventHandler<TimelineItemEventArgs> ItemRemoved;

    public ExampleSession(ExampleAccount account)
    {
        _Account = account;
        _SidebarAction = new ExampleSidebarAction(_Account, _MainTimeline);
    }

    public void OnItemProcessed(ProcessingResult result)
    {
        //Do work after an item has been processed by the pipeline.
    }
}
